mapper.select:
SELECT idConductor, nombre, password, telefono, fechaNacimiento, idEstatus, numLicencia
FROM transito.dbo.Conductor
WHERE telefono = #{telefono};

SELECT idConductor, nombre, password, telefono, fechaNacimiento, idEstatus, numLicencia
FROM transito.dbo.Conductor
WHERE telefono = #{telefono} AND password = #{password} AND idEstatus = 2;

SELECT idConductor, nombre, password, telefono, fechaNacimiento, idEstatus, numLicencia
FROM transito.dbo.Conductor
WHERE telefono = #{telefono} AND idEstatus = '1';

