mapper.select:
SELECT idVehiculo, numPlacas, modelo, marca, año AS year, color, idConductor
FROM transito.dbo.Vehiculo
WHERE numPlacas = #{numPLacas};

SELECT idVehiculo, numPlacas, modelo, marca, año AS year, color, idConductor
FROM transito.dbo.Vehiculo
WHERE idConductor = #{idConductor};

mapper.update:
UPDATE transito.dbo.Vehiculo SET
numPlacas = #{numPlacas}, modelo = #{modelo}, marca=#{marca}, año = #{year}, color=#{color}
WHERE idVehiculo = #{idVehiculo};
