mapper.select:
SELECT idVehiculo, numPlacas, modelo, marca, a単o AS year, color, idConductor
FROM transito.dbo.Vehiculo
WHERE numPlacas = #{numPLacas};

SELECT idVehiculo, numPlacas, modelo, marca, a単o AS year, color, idConductor
FROM transito.dbo.Vehiculo
WHERE idConductor = idConductor;

mapper.update:
UPDATE transito.dbo.Vehiculo SET
numPlacas = #{numPlacas}, modelo = #{modelo}, marca=#{marca}, a単o = #{year}, color=#{color}
WHERE idVehiculo = #{idVehiculo};
